const readlineSync = require ("readline-sync")

function completedTask(taskList){
    console.log("\n==========================================");
    console.log("           mark task as completed           ");
    console.log("\n==========================================");
    const idTaskCompleted = parseInt(readlineSync.question("Enter the ID of the completed task: "))
    let indice = taskList.findIndex((item) => item.id === idTaskCompleted)

    if(indice!= -1){
        taskList[indice].completed = true;
        console.log(`\nThe task with ID ${idTaskCompleted} was completed.`)
    } else {
        console.log(`\nThe task with ID ${idTaskCompleted} doesn't exist.`)
    }



    return(taskList)
}

module.exports = completedTask